curl -H "Authorization: Basic $1" http://localhost:9000/api/measures/component\?additionalFields=metrics%2cperiod%2cperiods\&component=$2\&metricKeys=$(cat $3 | sed 's/,/%2c/g') | jq | tee results1.json
curl -H "Authorization: Basic $1" http://localhost:9000/api/issues/search\?additionalFields=_all\&componentKeys=$2 | jq | tee results2.json
cat results1.json | jq -r '.component | [.key, .name, .qualifier, .measures[].metric, .measures[].value, .measures[].bestValue] | @csv' | tee results1.csv
cat results2.json | jq -r '.issues[] | [.key, .rule, .severity, .component, .project, .line, .hash, .textRange.startLine, .textRange.endLine, .textRange.startOffset, .textRange.endOffset, .status, .messsage, .effort, .debt, .author, .tags[], .transitions[], .actions[], .comments[], .creationDate, .updateDate, .type, .scope, .quickFixAvailable, .messageFormattings[], .flows[][][].textRange.startLine, .flows[][][].textRange.endLine, .flows[][][].textRange.startOffset, .flows[][][].textRange.endOffset ] | @csv' | tee results2.csv
